mapper.insert:
INSERT INTO MEMBER_INFO_TABLE
( USER_ID
, USER_EMAIL
, USER_NAME
, USER_PASSWORD
, USER_AGE
, USER_GENDER
, USER_INFOMATION 
, USER_regdate
, USER_lastLogin)
VALUES( 
  #{userId}
, #{userEmail}
, #{userName}
, #{userPassword} 
, #{userAge} 
, #{userGender} 
, #{userInfomation}
, DEFAULT 
, DEFAULT)

mapper.select:
SELECT * 
FROM MEMBER_INFO_TABLE
where USER_ID = #{userId}
and USER_PASSWORD = #{userPassword}

SELECT count(user_id)
FROM MEMBER_INFO_TABLE
where USER_ID = #{userId}

SELECT * 
FROM MEMBER_INFO_TABLE
where 1 = 1

	and USER_ID = #{userId}


	and USER_NAME = #{userName}


	and USER_EMAIL = #{userEmail}

mapper.update:
Update MEMBER_INFO_TABLE set
USER_PASSWORD = #{userPassword}
,USER_NAME = #{userName}
,USER_AGE = #{userAge}
,USER_GENDER = #{userGender}
,USER_EMAIL = #{userEmail}
,USER_INFOMATION = #{userInfomation}
where USER_ID = #{userId}

Update MEMBER_INFO_TABLE set
USER_INFOMATION = #{userInfomation}
where USER_ID = #{userId}
