mapper.insert:
INSERT INTO MEMBER_INFO_TABLE
( USER_ID
, USER_EMAIL
, USER_NAME
, USER_PASSWORD
, USER_AGE
, USER_INFOMATION 
, USER_regdate
, USER_lastLogin)
VALUES( 
  #{userId}
, #{userEmail}
, #{userName}
, #{userPassword} 
, #{userAge} 
, #{userInfomation}
, DEFAULT 
, DEFAULT)

mapper.select:
SELECT * 
FROM MEMBER_INFO_TABLE
where USER_ID = #{userId}
and USER_PASSWORD = #{userPassword}

SELECT count(user_id)
FROM MEMBER_INFO_TABLE
where USER_ID = #{userId}
